    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_widget)
        setSupportActionBar(findViewById(R.id.toolbar))

        var scrollTextView = findViewById<ScrollTextView>(R.id.scrollView)
        var scrollTextViewParams = SmartScrollTextViewParams()
        scrollTextViewParams.content = content
        scrollTextViewParams.textSize = 20
        scrollTextViewParams.scrollDirect =SmartScrollTextViewParams.SCROLL_RIGHT_TO_LEFT
        scrollTextView.initialization(scrollTextViewParams)
        scrollTextView.startScroll()
    }

    override fun onCreateOptionsMenu(menu: Menu): Boolean {
        // Inflate the menu; this adds items to the action bar if it is present.

        return true
    }


}